/* import React from "react"; */
import React, { useState } from "react";
import cx from "classnames";
import SliderContext from "./context";
import ShowDetailsButton from "./ShowDetailsButton";
import Mark from "./Mark";
import "./Item.scss";
import { render } from "node-sass";


const Item = ({ movie }) => (
  <SliderContext.Consumer props={{ movie }}>
    {({ onSelectSlide, currentSlide, elementRef }) => {
      const isActive = currentSlide && currentSlide.id === movie.id,
        transitionSize = movie.transitionSize,
        transitionEase = movie.transitionEase,
        transitionGrowth = movie.transitionWidth;

      console.log("transitionSize " + movie.transitionWidth);

      console.log("transform " + transitionSize + " ease " + transitionEase);



function setStyle(hovered){
  hovered ? transform = "scale(" + transitionGrowth + ")" : "scale(1.0)";
  return {
      transform: transform,
      transition:
        "transform " +
        transitionSize +
        "ms ease " +
        transitionEase +
        "ms"}
  }




      return (
        <div

        onMouseOut={() =>  setStyle(false)}
        onMouseOver={() => setStyle(true)}
        // {hovered ? (
        //  style={{transform:"scale(" + transitionGrowth + ")"}}
        //     transform: "scale(" + transitionGrowth + ")"
        // ): (
        //   style={{transform:"scale(1.0)"}}
        //   );
        // }

        // style={{
        //   transition:
        //     "transform " +
        //     transitionSize +
        //     "ms ease " +
        //     transitionEase +
        //     "ms"}}

          /* transform : "scale(" + transitionGrowth + ")" */

          ref={elementRef}
          className={cx("item", {
            "item--open": isActive,
          })}
        >
          <img src={movie.image} alt="" />
          <ShowDetailsButton onClick={() => onSelectSlide(movie)} />
          {isActive && <Mark />}
        </div>
      );
    }}
  </SliderContext.Consumer>
);

export default Item;
