/* import React from "react"; */
import React, { useState } from "react";
import cx from "classnames";
import SliderContext from "./context";
import ShowDetailsButton from "./ShowDetailsButton";
import Mark from "./Mark";
import "./Item.scss";

function getTime() {
  /*Use States, amount of time to toggle */
  const [toggleResult, setToggleResult] = useState({
    goldsponsorimageToggle: 200,
    silversponsorimageToggle: 350,
    bronzesponsorimageToggle: 500,
  });
}

const Item = ({ movie }) => (
  <SliderContext.Consumer props={{movie}}>
    {({ onSelectSlide, currentSlide, elementRef }) => {
      const isActive = currentSlide && currentSlide.id === props.movie.id,
        transitionSize = props.movie.transitionSize,
        transitionEase = props.movie.transitionEase;

      return (
        <div
          style={{
            transition:
              "transform " + { transitionSize } + "ease " + { transitionEase },
          }}
          ref={elementRef}
          className={cx("item", {
            "item--open": isActive,
          })}
        >
          <img src={props.movie.image} alt="" />
          <ShowDetailsButton onClick={() => onSelectSlide(props.movie)} />
          {isActive && <Mark />}
        </div>
      );
    }}
  </SliderContext.Consumer>
);

export default Item;
